#Cr�er la base de donn�e
CREATE OR REPLACE DATABASE forum CHARACTER SET = "utf8"  COLLATE = "utf8_general_ci";

USE forum;

#Cr�er les tables

CREATE OR REPLACE TABLE roles(
	id TINYINT PRIMARY KEY AUTO_INCREMENT,
	nom VARCHAR(30) UNIQUE KEY NOT NULL,
	created_at TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
	updated_at TIMESTAMP NULL
);
INSERT INTO roles(nom) VALUES
("Administrateur"),
("Modération"),
("Utilisateur");

CREATE OR REPLACE TABLE utilisateurs(
	id INT PRIMARY KEY AUTO_INCREMENT,
	pseudonyme VARCHAR(30) UNIQUE KEY NOT NULL,
	est_actif BOOLEAN DEFAULT FALSE,
	est_connecte BOOLEAN DEFAULT FALSE,
	courriel VARCHAR(100),
	mot_de_passe VARCHAR(30),
	created_at TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
	updated_at TIMESTAMP NULL,
	role_id TINYINT NOT NULL DEFAULT 3,
	CONSTRAINT fk_utilisateurs_role_id FOREIGN KEY (role_id) REFERENCES roles(id) ON UPDATE CASCADE ON DELETE CASCADE
);

CREATE OR REPLACE TABLE forums(
	id INT PRIMARY KEY AUTO_INCREMENT,
	titre VARCHAR(30) UNIQUE KEY NOT NULL,
	description VARCHAR(300) NOT NULL,
	created_at TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
	updated_at TIMESTAMP NULL
);

CREATE OR REPLACE TABLE sujets(
	id INT PRIMARY KEY AUTO_INCREMENT,
	titre VARCHAR(30) NOT NULL, 
	description VARCHAR(300) NOT NULL,
	created_at TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
	updated_at TIMESTAMP NULL,
	forum_id int NOT NULL,
	CONSTRAINT fk_sujets_forum_id FOREIGN KEY (forum_id) REFERENCES forums(id) ON UPDATE CASCADE ON DELETE CASCADE,
	utilisateur_id int NOT NULL,
	CONSTRAINT fk_sujets_utilisateur_id FOREIGN KEY (utilisateur_id) REFERENCES utilisateurs(id) ON UPDATE CASCADE ON DELETE CASCADE
);
CREATE UNIQUE INDEX sujets_titre_forum_id_unique ON sujets(forum_id,titre);


CREATE OR REPLACE TABLE messages(
	id INT PRIMARY KEY AUTO_INCREMENT,
	contenu VARCHAR(500) NOT NULL,
	created_at TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
	updated_at TIMESTAMP NULL,
	sujet_id int NOT NULL,
	utilisateur_id int NOT NULL,
	CONSTRAINT messages_sujet_id_foreign FOREIGN KEY (sujet_id) REFERENCES sujets(id) ON UPDATE CASCADE ON DELETE CASCADE,
	CONSTRAINT messages_utilisateur_id_foreign FOREIGN KEY (utilisateur_id) REFERENCES utilisateurs(id) ON UPDATE CASCADE ON DELETE CASCADE
);


